import React from "react";
import { Line } from "react-chartjs-2";

const Chart = ({ data }) => {
  // console.log(data.map((day) => day.temp.day));
  let chartData = {
    labels: [1, 2, 3, 4, 5, 6, 7, 8],
    datasets: [
      {
        label: "Temperature",
        data: data.map((day) => day.temp.day),
      },
    ],
  };

  // console.log(charData);

  return (
    <div>
      <h1>ok</h1>
      <Line
        data={chartData}
        options={{
          plugins: {
            title: {
              display: true,
              text: "Cryptocurrency prices",
            },
            legend: {
              display: false,
              position: "bottom",
              // options.scales[scaleId].grid
            },
          },
          scales: {
            scales: {
              x: {
                display: true,
                grid: {
                  display: false,
                },
              },
              y: {
                display: true,
                grid: {
                  display: false,
                },
              },
            },
          },
        }}
      />
    </div>
  );
};

export default Chart;

