import React from "react";
import moment from "moment";
import { DynamicWiIcon } from "./Icon";
import { icons } from "./icons";

const Daily = ({ day, size }) => {
  const { dt, humidity, temp } = day;
  const id = day.weather[0].id;

  return (
    <>
      {size > 1000 ? (
        moment.unix(dt).format("MMM Do") === moment().format("MMM Do") ? (
          <article className="daily">
            <h1>Today</h1>
            <DynamicWiIcon name={icons[id]} className="icon-daily" />
            <p>Humidity: {humidity} %</p>
          </article>
        ) : (
          <article className="daily">
            <h1>{moment.unix(dt).format("MMM Do")}</h1>
            <DynamicWiIcon name={icons[id]} className="icon-daily" />
            <p>Humidity: {humidity} %</p>
          </article>
        )
      ) : moment.unix(dt).format("MMM Do") === moment().format("MMM Do") ? (
        // <article>
        //   <h1>Today</h1>
        //   <p>
        //     {Math.round(temp.day)}
        //     <sup>
        //       <sup>o</sup>C
        //     </sup>
        //   </p>
        //   <DynamicWiIcon name={icons[id]} />
        //   <p>Humidity: {humidity} %</p>
        // </article>
        <></>
      ) : (
        <article className="daily">
          <h1>{moment.unix(dt).format("MMM Do")}</h1>
          <div className="ti">
            <DynamicWiIcon name={icons[id]} className="icon-daily" />
            <p>
              {Math.round(temp.day)}
              <sup>
                <sup>o</sup>C
              </sup>
            </p>
          </div>
          <p>Humidity: {humidity} %</p>
          <div className="underline"></div>
        </article>
      )}
    </>
  );
};

export default Daily;
