import { icons } from "./icons";
import { DynamicWiIcon } from "./Icon";

const Current = ({ time, cityName, data, index }) => {
  console.log(index);
  console.log(data.daily[index]);
  const { temp, state, humidity, wind_speed, id } = data;
  const capitalize = (s) => s && s[0].toUpperCase() + s.slice(1);

  return (
    <section>
      <h1>{cityName}</h1>
      <p className="time">{time}</p>
      <div className="tai">
        <DynamicWiIcon name={icons[id]} className="icon-current" />
        <p className="temp">   
          {Math.round(temp)}
          <sup>
            <sup>o</sup>C
          </sup>
        </p>
      </div>

      <p className="state">{capitalize(state)}</p>

      <div className="hiw">
        <p>Humidity: {humidity}%</p>
        <p>Wind Speed: {wind_speed} m/s</p>
      </div>
    </section>
  );
};

export default Current;
