import { Line } from "react-chartjs-2";
import moment from "moment";

const Chart = ({ data, setIndex }) => {
  let chartData = {
    labels: data.map((day) => (moment.unix(day.dt).format("MMM Do"))),
    datasets: [
      {
        label: "Temperature",
        data: data.map((day) => Math.round(day.temp.day)),
      },
    ],
  };

  return (
    <div>
      <Line
        data={chartData}
        options={{
	  onClick: function (evt, item) {
            if (item.length > 0) {
              setIndex(item[0].index);
            }
          },
          elements: {
            point: {
              pointRadius: 10,
            },
            line: {
              tension: 0.18,
            },
          },
          plugins: {
            title: {
              display: false,
              text: "Cryptocurrency prices",
            },
            legend: {
              display: false,
              position: "bottom",
            },
          },

          scales: {
            x: {
              display: true,
              grid: {
                display: false,
              },
	      ticks : {

	      },
	      gridLines: {
		      color:"red"
	      }
            },
            y: {
              display: true,
              grid: {
                display: false,
              },
              suggestedMin: 0,
              suggestedMax: 40,
            },
          },
        }}
      />
    </div>
  );
};

export default Chart;

